// Channels

import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import './login.scss';
import Popup from "reactjs-popup";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login/dist/facebook-login-render-props'
import API from '../../lib/api/cms';
//import 'react-tabs/style/react-tabs.css';

class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showTabLogin : true,
            email : '',
            password : '',
            name: '',
            errorMsg : '',

        };
    }

    responseGoogle = (e) =>{

    }

    responseFacebook = (e) => {
        
    }

    handleChangeName = (e) => {
        this.setState({
            name : e.target.value
        })
    }

    handleChangeEmail = (e) => {
        this.setState({
            email : e.target.value
        })
    }
    
    handleChangePassword = (e) => {
        this.setState({
            password : e.target.value
        })
    }

    handleSubmitLogin = (e) =>  {
        e.preventDefault();
        let data = {
            email : this.state.email,
            password : this.state.password,
        }
        API.login(data).then(response => {
            console.log(response);
        }).catch(error => {
        
            if(error.data.messages) {
                this.setState({
                    errorMsg : error.data.messages,
                })
            }
        });
    }

    handleSubmitRegister = (e) => {
        e.preventDefault();
        let data = {
            email : this.state.email,
            password : this.state.password,
            name : this.state.name,
        }
        API.register(data).then(response => {
            console.log(response);
        })
        .catch(error => {
            console.log(error)
            // if(error.data.messages) {
            //     this.setState({
            //         errorMsg : error.data.messages,
            //     })
            // }
        });
    }

    render () { 
        return (
        <Popup
        open={true}
        modal
        nested
        position="top center"
      >
        {close => (
          <div className="modal">
            <span className="close" onClick={() => {
                close
                this.props.showLoginPage()
            }}>
                X
            </span>
            <div className="header">
            </div>
            <div className="content row" style={{paddingBottom:"30px"}}>
                    {this.state.showTabLogin ? (
                        <div className="col-md-5">
                            <br/>
                            <span style={{fontWeight:'600',fontSize:'20px',marginTop:'10px'}}>Đăng nhập</span><br></br><br></br>
                            <span >Đăng nhập để theo dõi ,quản lý các sản phẩm yêu thích, nhận tin tức khuyến mãi ưu đãi sớm nhất</span><br/><br/>
                            <img className="mr-1" style={{marginLeft:'29px'}} src="/assets/images/login-icon.png" />
                        </div>
                    ) : (
                        <div className="col-md-5">
                            <br/>
                            <span style={{fontWeight:'600',fontSize:'20px',marginTop:'10px'}}>Tạo tài khoản</span><br></br><br></br>
                            <span >Tạo tài khoản để theo dõi, quản lý các sản phẩm yêu thích , nhận tin tức khuyến mãi ưu đãi sớm nhất</span><br/><br/>
                            <img className="mr-1" style={{marginLeft:'29px'}} src="/assets/images/login-icon.png" />
                        </div>
                    )}
                <div className="col-md-7">
                <Tabs>
            <TabList>
            <Tab onClick={() => {
                 this.setState({
                    showTabLogin:true
                 })
            }} >Đăng nhập</Tab>
            <Tab onClick={() => {
                this.setState({
                    showTabLogin:false
                })
            }}>Tạo tài khoản</Tab>
            </TabList>
        
            <TabPanel>
                <form onSubmit={this.handleSubmitLogin}>
                    {
                        this.state.errorMsg && <div className="row"><span style={{color:'red',marginBottom: '-30px',
                        marginLeft: '78px'}}>{this.state.errorMsg}</span></div>     
                    }
                
                <div className="row" style={{marginTop:'30px'}}>
                    <label className="col-md-2 mt-1" >Email</label> <input placeholder="Nhập email" value={this.state.email} onChange={this.handleChangeEmail} required className="form-control col-md-8" type="text" />      
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <label className="col-md-2 mt-1" >Mật khẩu</label> <input placeholder="Nhập mật khẩu" value={this.state.password} onChange={this.handleChangePassword} required className="form-control col-md-8" type="password" />
                </div>
                <div className="row" style={{marginTop:'5px'}} >
                    <p className="col-md-8 text-left" style={{marginLeft:'65px'}} >Quên mật khẩu <a href="#">Nhấn vào đây</a></p>
                </div>
                <div className="row" style={{marginTop:'10px'}} >
                    <button className="col-md-8" style={{backgroundColor:"#ff9340",marginLeft:'75px'}} >Đăng nhập</button>
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <span style={{marginLeft: "13rem"}}>Hoặc</span>
                </div>
                </form>
                <div className="row" style={{marginTop:'10px'}}>
                <FacebookLogin
                    appId="" //APP ID NOT CREATED YET
                    fields="name,email,picture"
                    buttonText="Đăng nhập bằng Facebook"
                    render={renderProps => (
                        <button className="col-md-8 fa fa-facebook" style={{backgroundColor:"#3b5998",marginLeft:'75px',paddingTop:'1px',paddingBottom:'1px'}} onClick={renderProps.onClick}><img style={{float:"left"}} src="data:image/svg+xml;base64,"></img><span style={{lineHeight:'30px'}}>Đăng nhập bằng Facebook</span></button>
                      )}
                    callback={this.responseFacebook()}
                />
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <GoogleLogin
                    clientId="72519489304-ms463f4qt5lv89cqt9l4pdir5huenrlt.apps.googleusercontent.com"
                    buttonText="Đăng nhập bằng Gmail"
                    onSuccess={this.responseGoogle()}
                    onFailure={this.responseGoogle()}
                    render={renderProps => (
                        <button className="col-md-8" style={{backgroundColor:"#bf2f2f",marginLeft:'75px'}} onClick={renderProps.onClick} >Đăng nhập bằng Gmail</button>
                      )}
                    cookiePolicy={'single_host_origin'}
                    />
                </div>
            </TabPanel>
            <TabPanel>
                <form onSubmit={this.handleSubmitRegister}>
                {
                    this.state.errorMsg && <div className="row"><span style={{color:'red',marginBottom: '-30px',
                    marginLeft: '78px'}}>{this.state.errorMsg}</span></div>     
                }
                <div className="row" style={{marginTop:'30px'}}>
                    <label className="col-md-2 mt-1" >Họ tên </label> <input placeholder="Nhập họ tên" value={this.state.name} onChange={this.handleChangeName} className="form-control col-md-8" type="text" />
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <label className="col-md-2 mt-1" >Email</label> <input placeholder="Nhập email" value={this.state.email} onChange={this.handleChangeEmail} required className="form-control col-md-8" type="text" />
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <label className="col-md-2 mt-1" >Mật khẩu</label> <input placeholder="Nhập mật khẩu" value={this.state.password} onChange={this.handleChangePassword} required className="form-control col-md-8" type="password" />
                </div>
                <div className="row" style={{marginTop:'10px'}} >
                    <button className="col-md-8" style={{backgroundColor:"#ff9340",marginLeft:'75px'}} >Tạo tài khoản</button>
                </div>
                <div  className="row" style={{marginTop:'10px'}}>
                    <span style={{marginLeft:'75px'}}>Đăng kí là bạn đã đồng ý với <a href="#">Điều khoản sử dụng Fundi.vn</a></span>
                </div>
                </form>
            </TabPanel>
        </Tabs>
                </div>
             
            </div>
          </div>
            )}
            </Popup>
        );
    }
}

export default Login;
