// Channels

import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import './login.scss';
import Popup from "reactjs-popup";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';
//import 'react-tabs/style/react-tabs.css';

class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showTabLogin : true,
        };

    }

    responseGoogle = (e) =>{

    }

    responseFacebook = (e) => {
        
    }

    render () { 
        return (
        <Popup
        open={true}
        modal
        nested
        position="top center"
      >
        {close => (
          <div className="modal">
            <span className="close" onClick={() => {
                close
                props.showLoginPage()
            }}>
                X
            </span>
            <div className="header">
            </div>
            <div className="content row">
                    {this.state.showTabLogin ? (
                        <div className="col-md-5">
                            <br/>
                            <span style={{fontWeight:'600',fontSize:'20px',marginTop:'10px'}}>Đăng nhập</span><br></br><br></br>
                            <span >Đăng nhập để theo dõi ,quản lý các sản phẩm yêu thích, nhận tin tức khuyến mãi ưu đãi sớm nhất</span><br/><br/>
                            <img className="mr-1" style={{marginLeft:'29px'}} src="/assets/images/login-icon.png" />
                        </div>
                    ) : (
                        <div className="col-md-5">
                            <br/>
                            <span style={{fontWeight:'600',fontSize:'20px',marginTop:'10px'}}>Tạo tài khoản</span><br></br><br></br>
                            <span >Tạo tài khoản để theo dõi, quản lý các sản phẩm yêu thích , nhận tin tức khuyến mãi ưu đãi sớm nhất</span><br/><br/>
                            <img className="mr-1" style={{marginLeft:'29px'}} src="/assets/images/login-icon.png" />
                        </div>
                    )}
                <div className="col-md-7">
                <Tabs>
            <TabList>
            <Tab onClick={() => {
                 this.setState({
                    showTabLogin:true
                 })
            }} >Đăng nhập</Tab>
            <Tab onClick={() => {
                this.setState({
                    showTabLogin:false
                })
            }}>Tạo tài khoản</Tab>
            </TabList>
        
            <TabPanel>
                <div className="row" style={{marginTop:'30px'}}>
                    <label className="col-md-2 mt-1" >Email</label> <input className="form-control col-md-8" type="text" />
                    
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <label className="col-md-2 mt-1" >Mật khẩu</label> <input className="form-control col-md-8" type="text" />
                </div>
                <div className="row" style={{marginTop:'5px'}} >
                    <p className="col-md-8 text-left" style={{marginLeft:'65px'}} >Quên mật khẩu <a href="#">Nhấn vào đây</a></p>
                </div>
                <div className="row" style={{marginTop:'10px'}} >
                    <button className="col-md-8" style={{backgroundColor:"#ff9340",marginLeft:'75px'}} >Đăng nhập</button>
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <span style={{marginLeft: "13rem"}}>Hoặc</span>
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                <div className="col-md-3"></div>
                <div className="col-md-8">
                <FacebookLogin
                    appId="" //APP ID NOT CREATED YET
                    fields="name,email,picture"
                    buttonText="Đăng nhập bằng Facebook"
                    callback={this.responseFacebook()}
                />
                </div>
                </div>
                <div className="row" style={{marginTop:'10px'}}>
                    <div className="col-md-3"></div>
                    <div className="col-md-8">
                    <GoogleLogin
                    clientId="72519489304-ms463f4qt5lv89cqt9l4pdir5huenrlt.apps.googleusercontent.com"
                    buttonText="Đăng nhập bằng Gmail"
                    onSuccess={this.responseGoogle()}
                    onFailure={this.responseGoogle()}
                    cookiePolicy={'single_host_origin'}
                    />
                    </div>
              
                </div>
            </TabPanel>
            <TabPanel>
            <h2>Any content 2</h2>
            </TabPanel>
        </Tabs>
                </div>
             
            </div>
          </div>
            )}
            </Popup>
        );
    }
}

export default Login;
