// Channels

import React from 'react';
import PropTypes from 'prop-types';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import Pagination from "react-js-pagination";
import './profile.scss';
import ReactPaginate from 'react-paginate';

const Profile = props => {
    console.log(props.orderList)
    return (
        <section className="profile">
            <div className="container">
            <Tabs>
            <TabList>
                <Tab onClick={() => {}} > <p>Thông tin tài khoản </p></Tab>
                <Tab onClick={() => {}}>Lịch sử đặt hàng</Tab>
                <Tab onClick={() => {}}>Tài khoản hoàn tiền</Tab>
            </TabList>           
            <TabPanel>
                <div className="row" style={{marginTop:'30px'}}>
                    <label className="col-md-2 mt-1" >Họ tên </label> <input  disabled="true"  value={props.user && props.user.name}  className="form-control col-md-8" type="text" />
                </div>
                <div className="row" style={{marginTop:'10px',marginBottom:"10px"}}>
                    <label className="col-md-2 mt-1" >Email</label> <input  disabled="true"   value={props.user && props.user.email}  className="form-control col-md-8" type="text" />
                </div>
                <div className="row" style={{marginTop:'10px',marginBottom:"10px"}}>
                    <label className="col-md-2 mt-1" >Số điện thoại</label> <input  disabled="true" value={props.user && props.user.phone_number} className="form-control col-md-8" type="text" />
                </div>
           </TabPanel>
           <TabPanel>
               {
                props.orderList && count(props.orderList.data) > 0 &&
                <table style={{width:"100%",marginBottom:"30px"}}>
                   <tr>
                        <th>Mã đơn hàng</th>
                        <th>Tên đối tác</th>
                        <th>Giá đơn hàng</th>
                        <th>Tiền hoàn lại</th>
                        <th>Ngày giao dịch</th>
                   </tr>
                   {
                     
                       props.orderList && props.orderList.data && props.orderList.data.map((item,key) =>       
                           (
                            <tr>
                                <td>{item.order_id}</td>
                                <td>{item.merchant_name}</td>
                                <td>{item.payout_amount}</td>
                                <td>{item.est_cashback}</td>
                                <td>{item.conversion_time}</td>
                            </tr>
                           )
                       )
                   }    
               </table>   
               }

                { props.orderList && props.orderList.data.length > 0 &&
                    <Pagination
                            activePage={props.orderList && props.orderList.current_page}
                            itemsCountPerPage={props.orderList && props.orderList.per_page}
                            totalItemsCount={props.orderList && props.orderList.total}
                            onChange={props.handlePageChange}
                        />
                }
            
           </TabPanel>
           <TabPanel>

           </TabPanel>
            </Tabs>
            </div>
        </section>
    );
};

export default Profile;
