import React, { Component, Fragment } from 'react';
import Head from 'next/head'
import API from '../../lib/api/cms';
import Profile from '../../components/Customer/Profile';
import Configuration from '../../lib/api/configuration'

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: [],
            orderList : [],        
            page : 0,
            status : "",
        };
    }

    getCookie = (cookieName) => {
        console.log(cookieName);
        var match = document.cookie.match(new RegExp('(^| )' + cookieName + '=([^;]+)'));
        if (match) return match[2];
    }

    deleteCookie = (name) => {
        document.cookie = name +'=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
      }

    componentDidMount() {
        console.log(this.getCookie('api_token'))
        Configuration.setHeader('Authorization', `Bearer ${this.getCookie('api_token')}`);
        this.getUserDetail();
        this.getOrderList();
    }

    getUserDetail() {
        API.getUserDetail().then(response => {
            this.setState({
                user : response.data
            })
        }).catch(error => {
            var data = error.response;
            if(data.data.messages) {
               console.log(data.data.messages)
            }
        });
    }

    getOrderList(page = 1) {
         API.getOrderList(page).then(response => {
            this.setState({
                orderList : response.data
            })
        }).catch(error => {
            var data = error.response;
            if(data.data.messages) {
               console.log(data.data.messages)
            }
        });
    }

    handlePageChange = (e) => {
        this.setState({
            page : e
        })
        this.getOrderList(e);
    }

    handleFilterChange = (e) => {
        let status = e.target.value;
        this.setState({
            status : status
        })
        API.getOrderList(this.state.page,status).then(response => {
            this.setState({
                orderList : response.data
            })
        }).catch(error => {
            var data = error.response;
            if(data.data.messages) {
               console.log(data.data.messages)
            }
        });
    }

    logout = (e) => {
        this.deleteCookie('api_token');
        this.deleteCookie('user_id');
        window.location.href = "/";
    }

    render() {
        return (
            <Fragment>
                <Head>
                    <style>
                        {`body {
                            background: url(${this.state.backgroundImage}) top center no-repeat fixed;
                            background-size: cover;
                        }
                        @media (max-width: 575.98px) {
                            #root > main {
                                padding-left: 0rem;
                            }
                        }`}
                    </style>
                </Head>
                <Profile orderList={this.state.orderList} user={this.state.user} logout={this.logout} handlePageChange={this.handlePageChange}/>
            </Fragment>
        );
    }
}

export default Home
