import React, { Component, Fragment } from 'react';
import Head from 'next/head'
import API from '../../lib/api/cms';
import Profile from '../../components/Customer/Profile';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: [],
            orderList : [],
        };
    }

    componentDidMount() {
        this.getUserDetail();
        this.getOrderList();
    }

    getUserDetail() {
        // API.getUserDetail(userId).then(response => {
            
        // }).catch(error => {
        //     var data = error.response;
        //     if(data.data.messages) {
        //        console.log(data.data.messages)
        //     }
        // });
    }

    getOrderList() {
        //  API.getOrderList(1).then(response => {
            
        // }).catch(error => {
        //     var data = error.response;
        //     if(data.data.messages) {
        //        console.log(data.data.messages)
        //     }
        // });
    }

    render() {
        return (
            <Fragment>
                <Head>
                    <style>
                        {`body {
                            background: url(${this.state.backgroundImage}) top center no-repeat fixed;
                            background-size: cover;
                        }
                        @media (max-width: 575.98px) {
                            #root > main {
                                padding-left: 0rem;
                            }
                        }`}
                    </style>
                </Head>
                <Profile orderList={this.state.orderList} user={this.state.user} />
            </Fragment>
        );
    }
}

export default Home
