import React, { Component, Fragment } from 'react';
import Head from 'next/head'
import API from '../../lib/api/cms';
import Profile from '../../components/Profile';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: [],
        };
    }

    componentDidMount() {
        this.getUserDetail()
    }

    getUserDetail(userId) {
        API.getUserDetail(userId).then(response => {
            if(response.data.api_token) {
                this.createCookieInHour('api_token',response.data.api_token,100000)
                this.setState({
                    openWindow:false
                })
            }
        }).catch(error => {
            var data = error.response;
            if(data.data.messages) {
                this.setState({
                    errorMsg : data.data.messages,
                })
            }
        });
    }

    
    render() {
        return (
            <Fragment>
                <Head>
                    <style>
                        {`body {
                            background: url(${this.state.backgroundImage}) top center no-repeat fixed;
                            background-size: cover;
                        }
                        @media (max-width: 575.98px) {
                            #root > main {
                                padding-left: 0rem;
                            }
                        }`}
                    </style>
                </Head>
                <Profile />
            </Fragment>
        );
    }
}

export default Home
