import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import MediaQuery from 'react-responsive';
import Head from 'next/head'
import Preloader from '../../components/Preloader';
import CMS from '../../lib/api/cms';
import GoogleAds from '../../components/GoogleAds/googleAds';
import Blogs from '../../components/Blogs/blogs' ;
import Carousel from '../../components/Carousel/carousel';
import Channels from '../../components/Channels/channels';
import DownloadApp from '../../components/DownloadApp/downloadApp';
import Products from '../../components/Products';
import {isMobile} from 'react-device-detect';
import {render} from 'react-dom';

import PromotionsList from '../../components/Promotions/PromotionsList'

//import FontAwesomeIcon from '@fortawesome/react-fontawesome';
//import { faHome  } from '@fortawesome/pro-regular-svg-icons';
import  HeaderMobileStickyNav from '../../components/Header/HeaderMobileStickyNav';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            carousel: [],
            carousel_sliders:[],
            promotions: [],
            launchers: [],
            products: [],
            sellers:[],
            posts:[],
            news: [],
            sliders:[],
            coupons:[],
            ms:0,
            secs: 3600,
            timer:0,
            isShowCountDown: false,
            isShowTicker : false,
            page : 1,
        };

    }

    componentDidMount() {
        this.getBanners();
        this.getCoupons();
        this.getSellers();
        this.getAllPosts();
    }

    componentWillMount() {
        this.interval = setInterval( () => {
            this.setState({ms: 1});
        }, 1000)
    }
    componentWillUnmount() {
        clearInterval(this.interval);
    }

    getBanners() {
        CMS.getBanner().then(response => {
            let sliders = response.data.data;
            sliders.map((item, key) => item.slider_type == "carousel" ?
                this.setState ({
                    carousel_sliders : item.slider_items
                }) : "")

            this.setState({
                sliders: response.data.data
            });
        });
    }

    getCoupons() {
        // CMS.getCoupons().then(response => {
        //     this.setState({
        //         coupons: response.data.data
        //     });
        // });
    }

    getSellers() {
        CMS.getSellers().then(response => {
            this.setState({
                sellers: response.data
            });
        });
    }

    getAllPosts() {
        CMS.getAllPosts().then(response => {
            this.setState({
                posts: response.data
            });
            // let posts = response.data;
            // posts.filter((item,key) => {
            //    CMS.getPostImages(item.featured_media).then(response => {
            //         item.image_src = response.data.source_url;
            //         return item;
            //    });
            // })
        });
    }
    showMore = (event) => {
        let page = this.state.page;
        this.setState({
            page : page + 1,
        });

          let queryCondition = {
              page : page + 1,
          }
          CMS.getCoupons(queryCondition).then(response => {
            this.setState({
                coupons: this.state.coupons.concat(response.data.data)
            });
          });
    }

    mobileCheck() {
        return isMobile
    }

    render() {
        return (
            <Fragment>
                <Head>
                    <style>
                        {`body {
                            background: url(${this.state.backgroundImage}) top center no-repeat fixed;
                            background-size: cover;
                        }
                        @media (max-width: 575.98px) {
                            #root > main {
                                padding-left: 0rem;
                            }
                        }`}
                    </style>
                </Head>
                <Carousel {...this.props} mobileCheck={this.mobileCheck} carousel={[...this.state.sliders]} carousel_sliders={this.state.carousel_sliders} />
                <Channels {...this.props} sellers={this.state.sellers}  />
                <DownloadApp />
            </Fragment>
        );
    }
}

export default Home
