// Channels

import React from 'react';
import PropTypes from 'prop-types';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import Pagination from "react-js-pagination";
import './profile.scss';

const Profile = props => {
    return (
        <section className="profile">
            <div className="container">
            <Tabs>
            <TabList>
                <Tab onClick={() => {}} > <p>Thông tin tài khoản </p></Tab>
                <Tab onClick={() => {}}>Lịch sử đặt hàng</Tab>
                <Tab onClick={() => {}}>Tài khoản hoàn tiền</Tab>
            </TabList>           
            <TabPanel>
                <div className="row" style={{marginTop:'30px'}}>
                    <label className="col-md-2 mt-1" >Họ tên </label> <input  disabled="true"  value={props.user && props.user.name}  className="form-control col-md-8" type="text" />
                </div>
                <div className="row" style={{marginTop:'10px',marginBottom:"10px"}}>
                    <label className="col-md-2 mt-1" >Email</label> <input  disabled="true"   value={props.user && props.user.email}  className="form-control col-md-8" type="text" />
                </div>
                <div className="row" style={{marginTop:'10px',marginBottom:"10px"}}>
                    <label className="col-md-2 mt-1" >Số điện thoại</label> <input  disabled="true" value={props.phone_number && props.user.phone_number} className="form-control col-md-8" type="text" />
                </div>
           </TabPanel>
           <TabPanel>
               <table>
                   <tr>
                        <th>Mã đơn hàng</th>
                        <th>Tên đối tác</th>
                        <th>Giá đơn hàng</th>
                        <th>Tiền hoàn lại</th>
                   </tr>
                   {
                       props.orderList && props.orderList.map((item,key) => {
                           <tr>
                               <td></td>
                               <td></td>
                               <td></td>
                           </tr>
                       })
                   }    
               </table>

               <div>
                    <Pagination
                        activePage={1}
                        itemsCountPerPage={10}
                        totalItemsCount={40}
                        pageRangeDisplayed={10}
                        onChange={props.handlePageChange}
                        itemClass="page-item"
                        linkClass="page-link"
                    />
             </div>
           </TabPanel>
           <TabPanel>

           </TabPanel>
            </Tabs>
            </div>
        </section>
    );
};

export default Profile;
